@kasta_cmd(
    pattern="noformat$",
    func=lambda e: e.is_reply,
    no_crash=True,
)
async def _(kst):
    reply = await kst.get_reply_message()
    if not getattr(reply, "text", None):
        return await kst.try_delete()
    text = reply.text
    await kst.eor(text, parse_mode=parse_pre)


@kasta_cmd(
    pattern="nospace$",
    func=lambda e: e.is_reply,
    no_crash=True,
)
async def _(kst):
    reply = await kst.get_reply_message()
    if not getattr(reply, "text", None):
        return await kst.try_delete()
    text = "".join(reply.text.split())
    await kst.eor(text)


@kasta_cmd(
    pattern="repeat(?: |$)(.*)",
    func=lambda e: e.is_reply,
    no_crash=True,
)
async def _(kst):
    count = kst.pattern_match.group(1)
    reply = await kst.get_reply_message()
    if not getattr(reply, "text", None):
        return await kst.try_delete()
    count = int(count) if count.isdecimal() else 1
    txt = reply.text
    text = txt + "\n"
    for _ in range(0, count - 1):
        text += txt + "\n"
    await kst.eor(text)


@kasta_cmd(
    pattern=r"count(?: |$)([\s\S]*)",
    no_crash=True,
)
async def _(kst):
    text = (await kst.get_reply_message()).text if kst.is_reply else kst.pattern_match.group(1)
    if not text:
        return await kst.try_delete()
    count = len(re.findall(r"(\S+)", text))
    text = f"**Count:** `{count}`"
    await kst.eor(text)


@kasta_cmd(
    pattern=r"(upper|lower|title|capital|camel|snake|kebab)(?: |$)([\s\S]*)",
    no_crash=True,
)
async def _(kst):
    text = (await kst.get_reply_message()).text if kst.is_reply else kst.pattern_match.group(2)
    if not text:
        return await kst.try_delete()
    cmd = kst.pattern_match.group(1)
    if cmd == "upper":
        text = text.upper()
    elif cmd == "lower":
        text = text.lower()
    elif cmd == "title":
        text = text.title()
    elif cmd == "capital":
        text = text.capitalize()
    elif cmd == "camel":
        text = camel(text)
    elif cmd == "snake":
        text = snake(text)
    elif cmd == "kebab":
        text = kebab(text)
    await kst.eor(text)


@kasta_cmd(
    pattern="(b64encode|b64e)(?: |$)(.*)",
    no_crash=False,
)
async def _(kst):
    text = (await kst.get_reply_message()).text if kst.is_reply else kst.pattern_match.group(2)
    if not text:
        return await kst.try_delete()
    text = b64encode(text.encode("utf-8")).decode()
    await kst.eor(text, parse_mode=parse_pre)


@kasta_cmd(
    pattern="(b64decode|b64d)(?: |$)(.*)",
    no_crash=False,
)
async def _(kst):
    text = (await kst.get_reply_message()).text if kst.is_reply else kst.pattern_match.group(2)
    if not text:
        return await kst.try_delete()
    try:
        text = b64decode(text).decode("utf-8", "replace")
    except (binascii.Error, ValueError) as err:
        text = f"Invalid Base64 data: {err}"
    await kst.eor(text, parse_mode=parse_pre)


@kasta_cmd(
    pattern="(morse|unmorse)(?: |$)(.*)",
    no_crash=True,
)
async def _(kst):
    text = (await kst.get_reply_message()).text if kst.is_reply else kst.pattern_match.group(2)
    if not text:
        return await kst.try_delete()
    cmd = kst.pattern_match.group(1)
    api = "en" if cmd == "morse" else "de"
    msg = await kst.eor("`...`")
    headers = {
        "User-Agent": "Python/{0[0]}.{0[1]} aiohttp/{1} getter/{2}".format(
            sys.version_info, aiohttp.__version__, __version__
        )
    }
    url = f"https://notapi.vercel.app/api/morse?{api}={text}"
    res = await Searcher(url=url, headers=headers, re_json=True)
    if not res:
        return await msg.eod("`Try again now!`")
    await msg.eor(res.get("result"))


